import React, { useState, useEffect } from 'react';
import './app.css';
// Constante da API
const API_BASE_URL = 'http://localhost:5000/api';
/**
 * Componente Principal - Gerencia o estado de autentica√ß√£o e navega√ß√£o
 */
function App() {
  const [token, setToken] = useState(null);
  const [page, setPage] = useState('login'); // 'login', 'register'
  const [error, setError] = useState('');

  // Efeito para verificar se j√° existe um token no localStorage
  useEffect(() => {
    const storedToken = localStorage.getItem('finance_token');
    if (storedToken) {
      setToken(storedToken);
    }
  }, []);

  const handleAuthSuccess = (newToken) => {
    localStorage.setItem('finance_token', newToken);
    setToken(newToken);
    setError('');
  };

  const handleLogout = () => {
    localStorage.removeItem('finance_token');
    setToken(null);
    setPage('login');
  };

  // Renderiza√ß√£o condicional
  if (token) {
    // 1. Se tem token (logado), mostra a p√°gina de finan√ßas
    return <FinancePage token={token} onLogout={handleLogout} />;
  }

  // 2. Se n√£o tem token, mostra a p√°gina de Login ou Cadastro
  return (
    <div className="auth-container">
      {page === 'login' ? (
        <AuthPage
          mode="login"
          onAuthSuccess={handleAuthSuccess}
          onError={setError}
          onSwitchPage={() => { setPage('register'); setError(''); }}
        />
      ) : (
        <AuthPage
          mode="register"
          onAuthSuccess={handleAuthSuccess}
          onError={setError}
          onSwitchPage={() => { setPage('login'); setError(''); }}
        />
      )}
      {error && <p className="auth-error">{error}</p>}

      {/* Estilos CSS embutidos para toda a aplica√ß√£o */}
      <GlobalStyles />
    </div>
  );
}

/**
 * Componente para Login e Cadastro
 */
function AuthPage({ mode, onAuthSuccess, onError, onSwitchPage }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const isLogin = mode === 'login';

  const handleSubmit = async (e) => {
    e.preventDefault();
    onError('');
    setIsLoading(true);

    if (!isLogin && password !== confirmPassword) {
      onError('As senhas n√£o coincidem!');
      setIsLoading(false);
      return;
    }

    const endpoint = isLogin ? '/auth/login' : '/auth/register';
    const payload = { email, password };

    try {
      const response = await fetch(`${API_BASE_URL}${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (response.ok) {
        onAuthSuccess(data.token);
      } else {
        onError(data.error || `Erro ao ${isLogin ? 'logar' : 'registrar'}`);
      }
    } catch (err) {
      onError('Erro de rede. O backend est√° rodando?');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-card">
      <h2>{isLogin ? 'Login' : 'Cadastro'}</h2>
      <p>{isLogin ? 'Acesse sua conta' : 'Crie uma nova conta'}</p>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="seu@email.com"
            required
          />
        </div>
        <div className="form-group">
          <label>Senha</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="********"
            required
          />
        </div>
        {!isLogin && (
          <div className="form-group">
            <label>Confirmar Senha</label>
            <input
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="********"
              required
            />
          </div>
        )}
        <button type="submit" className="btn-auth" disabled={isLoading}>
          {isLoading ? 'Carregando...' : (isLogin ? 'Entrar' : 'Registrar')}
        </button>
      </form>
      <button onClick={onSwitchPage} className="btn-switch-auth">
        {isLogin ? 'N√£o tem uma conta? Cadastre-se' : 'J√° tem uma conta? Fa√ßa login'}
      </button>
    </div>
  );
}

/**
 * Componente da P√°gina de Finan√ßas (Seu App.js antigo, agora protegido)
 */
function FinancePage({ token, onLogout }) {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [needsSeed, setNeedsSeed] = useState(false);

  const [formData, setFormData] = useState({
    description: '',
    amount: '',
    type: 'expense',
    category: '',
    date: new Date().toISOString().split('T')[0]
  });

  // Fun√ß√£o para criar cabe√ßalhos de autentica√ß√£o
  const getAuthHeaders = () => ({
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${token}`
  });

  // Carregar dados da API (Agora usa o token)
  const loadData = async () => {
    setLoading(true);
    setNeedsSeed(false);
    try {
      const headers = getAuthHeaders();

      // 1. Buscar Categorias
      const categoriesResponse = await fetch(`${API_BASE_URL}/categories`, { headers });

      if (categoriesResponse.status === 401) {
        onLogout(); // Token inv√°lido ou expirado
        return;
      }
      if (categoriesResponse.status === 404) {
        // Usu√°rio novo, precisa popular o banco
        setNeedsSeed(true);
      }
      if (categoriesResponse.ok) {
        const categoriesData = await categoriesResponse.json();
        setCategories(categoriesData);
      }

      // 2. Buscar Transa√ß√µes
      const transactionsResponse = await fetch(`${API_BASE_URL}/transactions`, { headers });
      if (transactionsResponse.ok) {
        const transactionsData = await transactionsResponse.json();
        setTransactions(transactionsData);
      }

      setError('');
    } catch (err) {
      setError('Erro ao carregar dados: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // Carrega os dados quando o componente monta
  useEffect(() => {
    loadData();
  }, []); // Depend√™ncia vazia, roda s√≥ uma vez

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Enviar nova transa√ß√£o (Agora usa o token)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.description || !formData.amount || !formData.category) {
      setError('Por favor, preencha todos os campos');
      return;
    }

    try {
      const newTransaction = {
        description: formData.description,
        amount: formData.amount, // O backend j√° trata o parseFloat
        type: formData.type,
        category: formData.category,
        date: formData.date
      };

      const response = await fetch(`${API_BASE_URL}/transactions`, {
        method: 'POST',
        headers: getAuthHeaders(),
        body: JSON.stringify(newTransaction),
      });

      if (response.ok) {
        const savedTransaction = await response.json();
        setTransactions(prev => [savedTransaction, ...prev]);
        // Reset form
        setFormData({
          description: '', amount: '', type: 'expense', category: '',
          date: new Date().toISOString().split('T')[0]
        });
        setError('');
      } else {
        const errData = await response.json();
        throw new Error(errData.error || 'Erro na API');
      }
    } catch (err) {
      setError('Erro ao adicionar transa√ß√£o: ' + err.message);
    }
  };

  // NOVA FUN√á√ÉO: Popular o banco com categorias padr√£o
  const handleSeedCategories = async () => {
    setError('');
    try {
      const response = await fetch(`${API_BASE_URL}/setup/seed-categories`, {
        headers: getAuthHeaders()
      });
      const data = await response.json();
      if (response.ok) {
        alert('Categorias criadas com sucesso! Carregando dados...');
        loadData(); // Recarrega os dados (agora com categorias)
      } else {
        throw new Error(data.error);
      }
    } catch (err) {
      setError('Erro ao criar categorias: ' + err.message);
    }
  };


  // Calcular totais (sem altera√ß√£o)
  const totals = transactions.reduce((acc, transaction) => {
    if (transaction.type === 'income') {
      acc.income += parseFloat(transaction.amount);
    } else {
      acc.expenses += parseFloat(transaction.amount);
    }
    return acc;
  }, { income: 0, expenses: 0 });
  const balance = totals.income - totals.expenses;

  // Filtrar categorias (sem altera√ß√£o)
  const getFilteredCategories = () => {
    return categories.filter(cat => cat.type === formData.type);
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">
          <h2>Carregando Sistema de Finan√ßas...</h2>
          <p>Autenticando e buscando dados...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <div className="header">
        <h1>üí∞ Sistema de Finan√ßas</h1>
        <button onClick={onLogout} className="btn-logout">
          Sair
        </button>
      </div>

      {error && <div className="error">{error}</div>}

      {/* NOVO: Bloco para popular categorias se for um usu√°rio novo */}
      {needsSeed && (
        <div className="card seed-card">
          <h3>Bem-vindo!</h3>
          <p>Parece que voc√™ √© um novo usu√°rio. Clique no bot√£o abaixo para criar suas categorias financeiras padr√£o (Sal√°rio, Alimenta√ß√£o, etc.).</p>
          <button onClick={handleSeedCategories} className="btn">
            Criar Categorias Padr√£o
          </button>
        </div>
      )}

      <div className="dashboard">
        {/* ... (c√≥digo dos cards de Saldo, Receitas, Despesas) ... */}
         <div className="card">
           <h3>Saldo Total</h3>
           <div className={`balance ${balance >= 0 ? 'positive' : 'negative'}`}>
             R$ {balance.toFixed(2)}
           </div>
         </div>
         <div className="card">
           <h3>Receitas</h3>
           <div className="balance positive">R$ {totals.income.toFixed(2)}</div>
         </div>
         <div className="card">
           <h3>Despesas</h3>
           <div className="balance negative">R$ {totals.expenses.toFixed(2)}</div>
         </div>
      </div>

      <div className="transaction-form">
        <h3>Adicionar Transa√ß√£o</h3>
        <form onSubmit={handleSubmit}>
          {/* ... (c√≥digo do formul√°rio: Descri√ß√£o, Valor, Tipo, etc.) ... */}
          <div className="form-group">
            <label>Descri√ß√£o</label>
            <input
              type="text" name="description" value={formData.description}
              onChange={handleInputChange} placeholder="Ex: Sal√°rio, Mercado" required
            />
          </div>
          <div className="form-group">
            <label>Valor (R$)</label>
            <input
              type="number" name="amount" value={formData.amount}
              onChange={handleInputChange} placeholder="0.00" step="0.01" required
            />
          </div>
          <div className="form-group">
            <label>Tipo</label>
            <select name="type" value={formData.type} onChange={handleInputChange}>
              <option value="expense">Despesa</option>
              <option value="income">Receita</option>
            </select>
          </div>
          <div className="form-group">
            <label>Categoria</label>
            <select name="category" value={formData.category} onChange={handleInputChange} required>
              <option value="">Selecione uma categoria</option>
              {getFilteredCategories().map(cat => (
                <option key={cat._id || cat.id} value={cat.name}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Data</label>
            <input
              type="date" name="date" value={formData.date}
              onChange={handleInputChange} required
            />
          </div>
          <button type="submit" className="btn">
            Adicionar Transa√ß√£o
          </button>
        </form>
      </div>

      <div className="transactions-list">
        <h3>√öltimas Transa√ß√µes ({transactions.length})</h3>
        {/* ... (c√≥digo da lista de transa√ß√µes) ... */}
        {transactions.length === 0 ? (
          <p>Nenhuma transa√ß√£o cadastrada</p>
        ) : (
          transactions.map(transaction => (
            <div key={transaction._id} className="transaction-item">
              <div className="transaction-info">
                <div className="transaction-description">
                  {transaction.description}
                </div>
                <div className="transaction-category">
                  {transaction.category} ‚Ä¢ {new Date(transaction.date).toLocaleDateString('pt-BR', { timeZone: 'UTC' })}
                </div>
              </div>
              <div className={`transaction-amount ${transaction.type === 'income' ? 'positive' : 'negative'}`}>
                {transaction.type === 'income' ? '+' : '-'} R$ {parseFloat(transaction.amount).toFixed(2)}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}


/**
 * Estilos Globais (CSS-in-JS)
 */
function GlobalStyles() {
  return (
    <style>{`
      :root {
        --cor-primaria: #4f46e5;
        --cor-primaria-hover: #4338ca;
        --cor-fundo: #f3f4f6;
        --cor-fundo-card: #ffffff;
        --cor-texto: #1f2937;
        --cor-texto-suave: #6b7280;
        --cor-borda: #e5e7eb;
        --cor-sucesso: #10b981;
        --cor-erro: #ef4444;
        --sombra: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
      }
      
      body {
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
        background-color: var(--cor-fundo);
        color: var(--cor-texto);
        margin: 0;
        padding: 0;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
      }

      /* --- Estilos da P√°gina de Autentica√ß√£o --- */
      
      .auth-container {
        display: flex;
        justify-content: center;
        align-items: center;
        min-height: 100vh;
        padding: 20px;
        box-sizing: border-box;
      }
      
      .auth-card {
        background: var(--cor-fundo-card);
        border-radius: 12px;
        box-shadow: var(--sombra);
        padding: 2.5rem;
        width: 100%;
        max-width: 420px;
        text-align: center;
      }
      
      .auth-card h2 {
        font-size: 1.75rem;
        font-weight: 700;
        margin-top: 0;
        margin-bottom: 0.5rem;
      }
      
      .auth-card p {
        color: var(--cor-texto-suave);
        margin-bottom: 2rem;
      }
      
      .auth-error {
        color: var(--cor-erro);
        background-color: #fee2e2;
        border-radius: 8px;
        padding: 0.75rem 1rem;
        margin-top: 1.5rem;
        text-align: center;
        font-weight: 500;
        border: 1px solid var(--cor-erro);
      }
      
      .btn-auth {
        width: 100%;
        padding: 0.75rem 1rem;
        font-size: 1rem;
        font-weight: 600;
        background-color: var(--cor-primaria);
        color: white;
        border: none;
        border-radius: 8px;
        cursor: pointer;
        transition: background-color 0.2s ease;
      }
      
      .btn-auth:hover {
        background-color: var(--cor-primaria-hover);
      }

      .btn-auth:disabled {
        background-color: #9ca3af;
        cursor: not-allowed;
      }
      
      .btn-switch-auth {
        background: none;
        border: none;
        color: var(--cor-primaria);
        font-weight: 600;
        cursor: pointer;
        margin-top: 1.5rem;
        padding: 0;
      }
      
      .btn-switch-auth:hover {
        text-decoration: underline;
      }
      

      /* --- Estilos da P√°gina de Finan√ßas --- */

      .app {
        max-width: 900px;
        margin: 0 auto;
        padding: 20px;
      }
      
      .loading {
        text-align: center;
        padding: 50px;
        font-size: 1.2rem;
      }

      .header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 20px;
      }
      
      .header h1 {
        margin: 0;
        font-size: 2rem;
      }
      
      .btn-logout {
        background: var(--cor-erro);
        color: white;
        border: none;
        padding: 0.5rem 1rem;
        border-radius: 8px;
        font-weight: 600;
        cursor: pointer;
      }
      .btn-logout:hover {
        background: #b91c1c;
      }

      .error {
        background-color: #fee2e2;
        color: var(--cor-erro);
        padding: 1rem;
        border-radius: 8px;
        border: 1px solid var(--cor-erro);
        margin-bottom: 20px;
        font-weight: 500;
      }
      
      .seed-card {
        background-color: #dbeafe;
        color: #1e40af;
        border: 1px solid #93c5fd;
      }

      .dashboard {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
        gap: 20px;
        margin-bottom: 30px;
      }

      .card {
        background-color: var(--cor-fundo-card);
        padding: 20px;
        border-radius: 12px;
        box-shadow: var(--sombra);
      }
      
      .card h3 {
        margin-top: 0;
        margin-bottom: 10px;
        color: var(--cor-texto-suave);
        font-size: 0.9rem;
        font-weight: 600;
        text-transform: uppercase;
      }
      
      .balance {
        font-size: 2rem;
        font-weight: 700;
      }
      
      .positive { color: var(--cor-sucesso); }
      .negative { color: var(--cor-erro); }

      .transaction-form {
        background-color: var(--cor-fundo-card);
        padding: 25px;
        border-radius: 12px;
        box-shadow: var(--sombra);
        margin-bottom: 30px;
      }
      
      .transaction-form h3 {
        margin-top: 0;
      }
      
      .transaction-form form {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
        gap: 20px;
      }
      
      .form-group {
        display: flex;
        flex-direction: column;
      }
      
      .form-group label {
        font-size: 0.875rem;
        font-weight: 600;
        margin-bottom: 5px;
      }
      
      .form-group input,
      .form-group select {
        padding: 0.75rem;
        border: 1px solid var(--cor-borda);
        border-radius: 8px;
        font-size: 1rem;
      }
      
      .btn {
        background-color: var(--cor-primaria);
        color: white;
        font-weight: 600;
        border: none;
        border-radius: 8px;
        padding: 0.75rem;
        cursor: pointer;
        transition: background-color 0.2s ease;
        font-size: 1rem;
        /* Ocupa a linha toda no grid */
        grid-column: 1 / -1; 
      }
      
      .btn:hover {
        background-color: var(--cor-primaria-hover);
      }
      
      .transactions-list h3 {
        border-bottom: 2px solid var(--cor-borda);
        padding-bottom: 10px;
      }

      .transaction-item {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 15px 0;
        border-bottom: 1px solid var(--cor-borda);
      }
      
      .transaction-item:last-child {
        border-bottom: none;
      }
      
      .transaction-description {
        font-weight: 600;
      }
      
      .transaction-category {
        font-size: 0.875rem;
        color: var(--cor-texto-suave);
      }
      
      .transaction-amount {
        font-weight: 700;
        font-size: 1.1rem;
      }

      /* Responsividade */
      @media (max-width: 600px) {
        .header {
          flex-direction: column;
          align-items: flex-start;
          gap: 10px;
        }
        .transaction-form form {
          grid-template-columns: 1fr;
        }
      }
    `}</style>
  );
}

export default App;
