import React, { useState, useEffect } from 'react';
import './app.css';

// URL da API - ajuste conforme necess√°rio
const API_URL = 'http://localhost:5000/api';

function App() {
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [formData, setFormData] = useState({
    description: '',
    amount: '',
    type: 'expense',
    category: '',
    date: new Date().toISOString().split('T')[0]
  });

  // Carregar dados da API
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);

      // Tentar carregar categorias da API
      try {
        const categoriesResponse = await fetch(`${API_URL}/categories`);
        if (categoriesResponse.ok) {
          const categoriesData = await categoriesResponse.json();
          setCategories(categoriesData);
        }
      } catch (err) {
        console.log('Erro ao carregar categorias, usando dados mockados');
      }

      // Tentar carregar transa√ß√µes da API
      try {
        const transactionsResponse = await fetch(`${API_URL}/transactions`);
        if (transactionsResponse.ok) {
          const transactionsData = await transactionsResponse.json();
          setTransactions(transactionsData);
        }
      } catch (err) {
        console.log('Erro ao carregar transa√ß√µes, usando dados mockados');
        // Dados mockados de fallback
        setTransactions([
          { id: 1, description: 'Sal√°rio', amount: '3000.00', type: 'income', category: 'Sal√°rio', date: '2024-01-15' },
          { id: 2, description: 'Mercado', amount: '350.50', type: 'expense', category: 'Alimenta√ß√£o', date: '2024-01-16' },
        ]);
      }

      setError('');
    } catch (err) {
      setError('Erro ao carregar dados: ' + err.message);
      // Dados mockados em caso de erro
      setCategories([
        { id: 1, name: 'Alimenta√ß√£o', type: 'expense' },
        { id: 2, name: 'Transporte', type: 'expense' },
        { id: 3, name: 'Sal√°rio', type: 'income' },
      ]);
      setTransactions([
        { id: 1, description: 'Sal√°rio', amount: '3000.00', type: 'income', category: 'Sal√°rio', date: '2024-01-15' },
        { id: 2, description: 'Mercado', amount: '350.50', type: 'expense', category: 'Alimenta√ß√£o', date: '2024-01-16' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.description || !formData.amount || !formData.category) {
      setError('Por favor, preencha todos os campos');
      return;
    }

    try {
      const newTransaction = {
        id: Date.now(),
        description: formData.description,
        amount: parseFloat(formData.amount).toFixed(2),
        type: formData.type,
        category: formData.category,
        date: formData.date
      };

      // Tentar salvar na API
      try {
        const response = await fetch(`${API_URL}/transactions`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newTransaction),
        });

        if (response.ok) {
          const savedTransaction = await response.json();
          setTransactions(prev => [savedTransaction, ...prev]);
        } else {
          throw new Error('Erro na API');
        }
      } catch (apiError) {
        // Fallback: adicionar localmente
        setTransactions(prev => [newTransaction, ...prev]);
      }

      // Reset form
      setFormData({
        description: '',
        amount: '',
        type: 'expense',
        category: '',
        date: new Date().toISOString().split('T')[0]
      });

      setError('');
    } catch (err) {
      setError('Erro ao adicionar transa√ß√£o: ' + err.message);
    }
  };

  // Calcular totais
  const totals = transactions.reduce((acc, transaction) => {
    if (transaction.type === 'income') {
      acc.income += parseFloat(transaction.amount);
    } else {
      acc.expenses += parseFloat(transaction.amount);
    }
    return acc;
  }, { income: 0, expenses: 0 });

  const balance = totals.income - totals.expenses;

  const getFilteredCategories = () => {
    return categories.filter(cat => cat.type === formData.type);
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">
          <h2>Carregando Sistema de Finan√ßas...</h2>
          <p>Conectando com o backend...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      {/* MENSAGEM DE DEBUG - REMOVER DEPOIS QUE TUDO ESTIVER FUNCIONANDO */}
      <div className="debug-test">
        ‚úÖ CSS E REACT FUNCIONANDO! Se voc√™ v√™ esta mensagem, est√° tudo OK!
      </div>

      <div className="header">
        <h1>üí∞ Sistema de Finan√ßas</h1>
        <p>Controle suas receitas e despesas</p>
        <small>Backend: {API_URL}</small>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="dashboard">
        <div className="card">
          <h3>Saldo Total</h3>
          <div className={`balance ${balance >= 0 ? 'positive' : 'negative'}`}>
            R$ {balance.toFixed(2)}
          </div>
        </div>

        <div className="card">
          <h3>Receitas</h3>
          <div className="balance positive">R$ {totals.income.toFixed(2)}</div>
        </div>

        <div className="card">
          <h3>Despesas</h3>
          <div className="balance negative">R$ {totals.expenses.toFixed(2)}</div>
        </div>
      </div>

      <div className="transaction-form">
        <h3>Adicionar Transa√ß√£o</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Descri√ß√£o</label>
            <input
              type="text"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="Ex: Sal√°rio, Mercado, etc."
              required
            />
          </div>

          <div className="form-group">
            <label>Valor (R$)</label>
            <input
              type="number"
              name="amount"
              value={formData.amount}
              onChange={handleInputChange}
              placeholder="0.00"
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label>Tipo</label>
            <select name="type" value={formData.type} onChange={handleInputChange}>
              <option value="expense">Despesa</option>
              <option value="income">Receita</option>
            </select>
          </div>

          <div className="form-group">
            <label>Categoria</label>
            <select
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              required
            >
              <option value="">Selecione uma categoria</option>
              {getFilteredCategories().map(cat => (
                <option key={cat.id || cat._id} value={cat.name}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Data</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              required
            />
          </div>

          <button type="submit" className="btn">
            Adicionar Transa√ß√£o
          </button>
        </form>
      </div>

      <div className="transactions-list">
        <h3>√öltimas Transa√ß√µes ({transactions.length})</h3>
        {transactions.length === 0 ? (
          <p>Nenhuma transa√ß√£o cadastrada</p>
        ) : (
          transactions.map(transaction => (
            <div key={transaction.id || transaction._id} className="transaction-item">
              <div className="transaction-info">
                <div className="transaction-description">
                  {transaction.description}
                </div>
                <div className="transaction-category">
                  {transaction.category} ‚Ä¢ {new Date(transaction.date).toLocaleDateString('pt-BR')}
                </div>
              </div>
              <div className={`transaction-amount ${transaction.type === 'income' ? 'positive' : 'negative'}`}>
                {transaction.type === 'income' ? '+' : '-'} R$ {parseFloat(transaction.amount).toFixed(2)}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
